import{_ as e,c as r,o,a as t}from"./app.b8d26054.js";const a="/guo-notes/docker/image-20230323145750670.png",i="/guo-notes/docker/image-20210731153059464.png",c="/guo-notes/docker/image-20210731153743354.png",n="/guo-notes/docker/image-20210731154257653.png",f=JSON.parse('{"title":"Docker\u67B6\u6784","description":"","frontmatter":{"typora-root-url":"..\\\\..\\\\public"},"headers":[{"level":2,"title":"\u4E00\u3001\u955C\u50CF\uFF08Image\uFF09","slug":"\u4E00\u3001\u955C\u50CF\uFF08image\uFF09","link":"#\u4E00\u3001\u955C\u50CF\uFF08image\uFF09","children":[]},{"level":2,"title":"\u4E8C\u3001\u5BB9\u5668\uFF08Container\uFF09","slug":"\u4E8C\u3001\u5BB9\u5668\uFF08container\uFF09","link":"#\u4E8C\u3001\u5BB9\u5668\uFF08container\uFF09","children":[]},{"level":2,"title":"\u4E09\u3001\u4ED3\u5E93\uFF08Repository\uFF09","slug":"\u4E09\u3001\u4ED3\u5E93\uFF08repository\uFF09","link":"#\u4E09\u3001\u4ED3\u5E93\uFF08repository\uFF09","children":[]},{"level":2,"title":"\u56DB\u3001Docker\u67B6\u6784","slug":"\u56DB\u3001docker\u67B6\u6784","link":"#\u56DB\u3001docker\u67B6\u6784","children":[]}],"relativePath":"classify/Docker/Docker\u67B6\u6784.md","lastUpdated":1679569957000}'),s={name:"classify/Docker/Docker\u67B6\u6784.md"},p=t('<h1 id="docker\u67B6\u6784" tabindex="-1">Docker\u67B6\u6784 <a class="header-anchor" href="#docker\u67B6\u6784" aria-hidden="true">#</a></h1><h2 id="\u4E00\u3001\u955C\u50CF\uFF08image\uFF09" tabindex="-1">\u4E00\u3001\u955C\u50CF\uFF08Image\uFF09 <a class="header-anchor" href="#\u4E00\u3001\u955C\u50CF\uFF08image\uFF09" aria-hidden="true">#</a></h2><p>Docker\u7684\u955C\u50CF\u6982\u5FF5\u7C7B\u4F3C\u4E8E\u865A\u62DF\u673A\u91CC\u7684\u955C\u50CF\uFF0C\u662F\u4E00\u4E2A\u53EA\u8BFB\u7684\u6A21\u677F\uFF0C\u4E00\u4E2A\u72EC\u7ACB\u7684\u6587\u4EF6\u7CFB\u7EDF\uFF0C\u5305\u62EC\u8FD0\u884C\u5BB9\u5668\u6240\u9700\u7684\u6570\u636E\uFF0C\u53EF\u4EE5\u7528\u6765\u521B\u5EFA\u65B0\u7684\u5BB9\u5668\u3002</p><p>\u4F8B\u5982\uFF1A\u4E00\u4E2A\u955C\u50CF\u53EF\u4EE5\u5305\u542B\u4E00\u4E2A\u5B8C\u6574\u7684 Ubuntu \u64CD\u4F5C\u7CFB\u7EDF\u73AF\u5883\uFF0C\u91CC\u9762\u4EC5\u5B89\u88C5\u4E86 Mysql \u6216\u7528\u6237\u9700\u8981\u7684\u5176\u5B83\u5E94\u7528\u7A0B\u5E8F\u3002</p><p>Docker \u7684\u955C\u50CF\u5B9E\u9645\u4E0A\u7531\u4E00\u5C42\u4E00\u5C42\u7684\u6587\u4EF6\u7CFB\u7EDF\u7EC4\u6210\uFF0C\u8FD9\u79CD\u5C42\u7EA7\u7684\u6587\u4EF6\u7CFB\u7EDF\u88AB\u79F0\u4E3A UnionFS\u3002</p><p>\u955C\u50CF\u53EF\u4EE5\u57FA\u4E8E Dockerfile \u6784\u5EFA\uFF0CDockerfile \u662F\u4E00\u4E2A\u63CF\u8FF0\u6587\u4EF6\uFF0C\u91CC\u9762\u5305\u542B\u82E5\u5E72\u6761\u547D\u4EE4\uFF0C\u6BCF\u6761\u547D\u4EE4\u90FD\u4F1A\u5BF9\u57FA\u7840\u6587\u4EF6\u7CFB\u7EDF\u521B\u5EFA\u65B0\u7684\u5C42\u6B21\u7ED3\u6784\u3002</p><p>Docker\u63D0\u4F9B\u4E86\u4E00\u4E2A\u5F88\u7B80\u5355\u7684\u673A\u5236\u6765\u521B\u5EFA\u955C\u50CF\u6216\u8005\u66F4\u65B0\u73B0\u6709\u7684\u955C\u50CF\uFF0C\u7528\u6237\u751A\u81F3\u53EF\u4EE5\u76F4\u63A5\u4ECE\u5176\u4ED6\u4EBA\u90A3\u91CC\u4E0B\u8F7D\u4E00\u4E2A\u5DF2\u7ECF\u505A\u597D\u7684\u955C\u50CF\u6765\u76F4\u63A5\u4F7F\u7528\u3002</p><p><img src="'+a+'" alt="image-20230323145750670"></p><p>\u5982\u4E0A\u56FE\u5C55\u793A\u4E86 Docker \u955C\u50CF\u7684\u7CFB\u7EDF\u7ED3\u6784\u3002\u955C\u50CF\u7684\u6700\u5E95\u5C42\u5FC5\u987B\u662F\u4E00\u4E2A\u79F0\u4E3A\u542F\u52A8\u6587\u4EF6\u7CFB\u7EDF( \u5185\u6838 bootfs )\u7684\u955C\u50CF\uFF0C\u7528\u6237\u4E0D\u4F1A\u4E0E\u8FD9\u4E00\u5C42\u76F4\u63A5\u6253\u4EA4\u9053\u3002bootfs \u7684\u4E0A\u5C42\u955C\u50CF\u53EB\u4F5C\u6839\u955C\u50CF( rootfs )\uFF0C\u5B83\u5728\u901A\u5E38\u60C5\u51B5\u4E0B\u662F\u4E00\u4E2A\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u5982Ubuntu\u3001Debian \u548C CentOS \u7B49\uFF0C\u7528\u6237\u7684\u955C\u50CF\u5FC5\u987B\u6784\u5EFA\u4E8E\u6839\u955C\u50CF\u4E4B\u4E0A\u3002\u56FE\u4E2D\u6240\u793A\u7684\u955C\u50CF1\u662F\u901A\u8FC7\u5728\u6839\u955C\u50CF Ubuntu\u4E0A\u5B89\u88C5 MySQL \u6765\u521B\u5EFA\u7684\u3002\u5728\u955C\u50CF1\u7684\u57FA\u7840\u4E0A\u518D\u5B89\u88C5\u4E00\u4E2A redis\uFF0C\u5C31\u53C8\u521B\u5EFA\u4E86\u955C\u50CF2\u3002\u5229\u7528\u955C\u50CF2\u542F\u52A8\u7684\u5BB9\u5668\u91CC\u9762\u8FD0\u884C\u7684\u662F\u4E00\u4E2A\u5DF2\u7ECF\u5B89\u88C5\u597D\u4E86 MySQL \u548C Nginx \u7684 CentOS \u7CFB\u7EDF\u3002</p><h2 id="\u4E8C\u3001\u5BB9\u5668\uFF08container\uFF09" tabindex="-1">\u4E8C\u3001\u5BB9\u5668\uFF08Container\uFF09 <a class="header-anchor" href="#\u4E8C\u3001\u5BB9\u5668\uFF08container\uFF09" aria-hidden="true">#</a></h2><p>\u955C\u50CF\u4E2D\u7684\u5E94\u7528\u7A0B\u5E8F\u8FD0\u884C\u540E\u5F62\u6210\u7684\u8FDB\u7A0B\u5C31\u662F<strong>\u5BB9\u5668</strong>\uFF0C\u53EA\u662FDocker\u4F1A\u7ED9\u5BB9\u5668\u8FDB\u7A0B\u505A\u9694\u79BB\uFF0C\u5BF9\u5916\u4E0D\u53EF\u89C1\u3002</p><p>Docker \u5BB9\u5668\u7C7B\u4F3C\u865A\u62DF\u673A\uFF0C\u53EF\u4EE5\u652F\u6301\u7684\u64CD\u4F5C\u5305\u62EC\u542F\u52A8\uFF0C\u505C\u6B62\uFF0C\u5220\u9664\u7B49\u3002\u6BCF\u4E2A\u5BB9\u5668\u95F4\u662F\u76F8\u4E92\u9694\u79BB\u7684\uFF0C\u5BB9\u5668\u4E2D\u4F1A\u8FD0\u884C\u7279\u5B9A\u7684\u5E94\u7528\uFF0C\u5305\u542B\u7279\u5B9A\u5E94\u7528\u7684\u4EE3\u7801\u53CA\u6240\u9700\u7684\u4F9D\u8D56\u6587\u4EF6\u3002\u53EF\u4EE5\u628A\u5BB9\u5668\u770B\u505A\u662F\u4E00\u4E2A\u7B80\u6613\u7248\u7684 Linux \u73AF\u5883(\u5305\u62EC root \u7528\u6237\u6743\u9650\u3001\u8FDB\u7A0B\u7A7A\u95F4\u3001\u7528\u6237\u7A7A\u95F4\u548C\u7F51\u7EDC\u7A7A\u95F4\u7B49)\u548C\u8FD0\u884C\u5728\u5176\u4E2D\u7684\u5E94\u7528\u7A0B\u5E8F\u3002</p><p>\u4E00\u5207\u5E94\u7528\u6700\u7EC8\u90FD\u662F\u4EE3\u7801\u7EC4\u6210\uFF0C\u90FD\u662F\u786C\u76D8\u4E2D\u7684\u4E00\u4E2A\u4E2A\u7684\u5B57\u8282\u5F62\u6210\u7684<strong>\u6587\u4EF6</strong>\u3002\u53EA\u6709\u8FD0\u884C\u65F6\uFF0C\u624D\u4F1A\u52A0\u8F7D\u5230\u5185\u5B58\uFF0C\u5F62\u6210\u8FDB\u7A0B\u3002</p><p>\u800C<strong>\u955C\u50CF</strong>\uFF0C\u5C31\u662F\u628A\u4E00\u4E2A\u5E94\u7528\u5728\u786C\u76D8\u4E0A\u7684\u6587\u4EF6\u3001\u53CA\u5176\u8FD0\u884C\u73AF\u5883\u3001\u90E8\u5206\u7CFB\u7EDF\u51FD\u6570\u5E93\u6587\u4EF6\u4E00\u8D77\u6253\u5305\u5F62\u6210\u7684\u6587\u4EF6\u5305\u3002\u8FD9\u4E2A\u6587\u4EF6\u5305\u662F\u53EA\u8BFB\u7684\u3002</p><p><strong>\u5BB9\u5668</strong>\uFF0C\u5C31\u662F\u5C06\u8FD9\u4E9B\u6587\u4EF6\u4E2D\u7F16\u5199\u7684\u7A0B\u5E8F\u3001\u51FD\u6570\u52A0\u8F7D\u5230\u5185\u5B58\u4E2D\u5141\u8BB8\uFF0C\u5F62\u6210\u8FDB\u7A0B\uFF0C\u53EA\u4E0D\u8FC7\u8981\u9694\u79BB\u8D77\u6765\u3002\u56E0\u6B64\u4E00\u4E2A\u955C\u50CF\u53EF\u4EE5\u542F\u52A8\u591A\u6B21\uFF0C\u5F62\u6210\u591A\u4E2A\u5BB9\u5668\u8FDB\u7A0B\u3002</p><p><img src="'+i+'" alt="image-20210731153059464"></p><p>\u4F8B\u5982\u4F60\u4E0B\u8F7D\u4E86\u4E00\u4E2A QQ\uFF0C\u5982\u679C\u6211\u4EEC\u5C06 QQ \u5728\u78C1\u76D8\u4E0A\u7684\u8FD0\u884C<strong>\u6587\u4EF6</strong>\u53CA\u5176\u8FD0\u884C\u7684\u64CD\u4F5C\u7CFB\u7EDF\u4F9D\u8D56\u6253\u5305\uFF0C\u5F62\u6210 QQ \u955C\u50CF\u3002\u7136\u540E\u4F60\u53EF\u4EE5\u542F\u52A8\u591A\u6B21\uFF0C\u53CC\u5F00\u3001\u751A\u81F3\u4E09\u5F00 QQ\u3002</p><h2 id="\u4E09\u3001\u4ED3\u5E93\uFF08repository\uFF09" tabindex="-1">\u4E09\u3001\u4ED3\u5E93\uFF08Repository\uFF09 <a class="header-anchor" href="#\u4E09\u3001\u4ED3\u5E93\uFF08repository\uFF09" aria-hidden="true">#</a></h2><p>\u5982\u679C\u4F7F\u7528\u8FC7 Git \u548C GitHub \u5C31\u5F88\u5BB9\u6613\u7406\u89E3 Docker \u7684\u4ED3\u5E93\u6982\u5FF5\u3002Docker \u4ED3\u5E93\u7684\u6982\u5FF5\u8DDF Git \u7C7B\u4F3C\uFF0C\u6CE8\u518C\u670D\u52A1\u5668\u53EF\u4EE5\u7406\u89E3\u4E3A GitHub \u8FD9\u6837\u7684\u6258\u7BA1\u670D\u52A1\u3002</p><p>Docker \u4ED3\u5E93\u662F\u7528\u6765\u5305\u542B\u955C\u50CF\u7684\u4F4D\u7F6E\uFF0CDocker \u63D0\u4F9B\u4E00 \u4E2A\u6CE8\u518C\u670D\u52A1\u5668(Register) \u6765\u4FDD\u5B58\u591A\u4E2A\u4ED3\u5E93\uFF0C\u6BCF\u4E2A\u4ED3\u5E93\u53C8\u53EF\u4EE5\u5305\u542B\u591A\u4E2A\u5177\u5907\u4E0D\u540C tag \u7684\u955C\u50CF\u3002</p><p>Docker \u8FD0\u884C\u4E2D\u4F7F\u7528\u7684\u9ED8\u8BA4\u4ED3\u5E93\u662F DockerHub \u516C\u5171\u4ED3\u5E93\u3002\u4ED3\u5E93\u652F\u6301\u7684\u64CD\u4F5C\u7C7B\u4F3C Git\uFF0C\u5F53\u7528\u6237\u521B\u5EFA\u4E86\u81EA\u5DF1\u7684\u955C\u50CF\u4E4B\u540E\u5C31\u53EF\u4EE5\u4F7F\u7528 push \u547D\u4EE4\u5C06\u5B83\u4E0A\u4F20\u5230\u516C\u6709\u6216\u8005\u79C1\u6709\u4ED3\u5E93\uFF0C\u8FD9\u6837\u4E0B\u6B21\u5728\u53E6\u5916\u4E00\u53F0\u673A\u5668\u4E0A\u4F7F\u7528\u8FD9\u4E2A\u955C\u50CF\u65F6\u5019\uFF0C\u53EA\u9700\u8981\u4ECE\u4ED3\u5E93\u4E0A pull \u4E0B\u6765\u5C31\u53EF\u4EE5\u4E86\u3002</p><ul><li><p>DockerHub \u662F\u4E00\u4E2A\u5B98\u65B9\u7684 Docker \u955C\u50CF\u7684\u6258\u7BA1\u5E73\u53F0\uFF0C\u8FD9\u6837\u7684\u5E73\u53F0\u79F0\u4E3A Docker Registry\u3002</p></li><li><p>\u56FD\u5185\u4E5F\u6709\u7C7B\u4F3C\u4E8EDockerHub \u7684\u516C\u5F00\u670D\u52A1\uFF0C\u6BD4\u5982 <a href="https://c.163yun.com/hub" target="_blank" rel="noreferrer">\u7F51\u6613\u4E91\u955C\u50CF\u670D\u52A1</a>\u3001<a href="https://cr.console.aliyun.com/" target="_blank" rel="noreferrer">\u963F\u91CC\u4E91\u955C\u50CF\u5E93</a>\u7B49\u3002</p></li></ul><p><img src="'+c+'" alt="image-20210731153743354"></p><h2 id="\u56DB\u3001docker\u67B6\u6784" tabindex="-1">\u56DB\u3001Docker\u67B6\u6784 <a class="header-anchor" href="#\u56DB\u3001docker\u67B6\u6784" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u8981\u4F7F\u7528Docker\u6765\u64CD\u4F5C\u955C\u50CF\u3001\u5BB9\u5668\uFF0C\u5C31\u5FC5\u987B\u8981\u5B89\u88C5Docker\u3002</p><p>Docker\u662F\u4E00\u4E2ACS\u67B6\u6784\u7684\u7A0B\u5E8F\uFF0C\u7531\u4E24\u90E8\u5206\u7EC4\u6210\uFF1A</p><ul><li><p>\u670D\u52A1\u7AEF(server)\uFF1ADocker\u5B88\u62A4\u8FDB\u7A0B\uFF0C\u8D1F\u8D23\u5904\u7406Docker\u6307\u4EE4\uFF0C\u7BA1\u7406\u955C\u50CF\u3001\u5BB9\u5668\u7B49</p></li><li><p>\u5BA2\u6237\u7AEF(client)\uFF1A\u901A\u8FC7\u547D\u4EE4\u6216RestAPI\u5411Docker\u670D\u52A1\u7AEF\u53D1\u9001\u6307\u4EE4\u3002\u53EF\u4EE5\u5728\u672C\u5730\u6216\u8FDC\u7A0B\u5411\u670D\u52A1\u7AEF\u53D1\u9001\u6307\u4EE4\u3002</p></li></ul><p>\u5982\u56FE\uFF1A</p><p><img src="'+n+'" alt="image-20210731154257653"></p>',29),l=[p];function d(k,g,h,u,_,D){return o(),r("div",null,l)}const b=e(s,[["render",d]]);export{f as __pageData,b as default};
