import{_ as e,c as r,o as t,a as n}from"./app.8b821eb2.js";const o="/guo-notes/kubenetes/image-20200505183738289-1680166605145.png",s="/guo-notes/kubenetes/image-20200524150339551-1680166605145.png",p="/guo-notes/kubenetes/image-20200406232838722-1680166605145.png",i="/guo-notes/kubenetes/image-20200526203726071-1680166605145.png",l="/guo-notes/Kubenetes/image-20200406184656917.png",x=JSON.parse('{"title":"Kubernetes\u4ECB\u7ECD","description":"","frontmatter":{"typora-root-url":"..\\\\..\\\\public","typora-copy-images-to":"..\\\\..\\\\public\\\\kubenetes"},"headers":[{"level":2,"title":"\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8","slug":"\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8","link":"#\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8","children":[]},{"level":2,"title":"\u4E8C\u3001kubernetes\u7B80\u4ECB","slug":"\u4E8C\u3001kubernetes\u7B80\u4ECB","link":"#\u4E8C\u3001kubernetes\u7B80\u4ECB","children":[]},{"level":2,"title":"\u4E09\u3001kubernetes\u7EC4\u4EF6","slug":"\u4E09\u3001kubernetes\u7EC4\u4EF6","link":"#\u4E09\u3001kubernetes\u7EC4\u4EF6","children":[]},{"level":2,"title":"\u56DB\u3001kubernetes\u6982\u5FF5","slug":"\u56DB\u3001kubernetes\u6982\u5FF5","link":"#\u56DB\u3001kubernetes\u6982\u5FF5","children":[]}],"relativePath":"classify/Kubernetes/Kubernetes\u4ECB\u7ECD.md","lastUpdated":1680256036000}'),a={name:"classify/Kubernetes/Kubernetes\u4ECB\u7ECD.md"},g=n('<p>\u7B14\u8BB0\u6765\u6E90\uFF1A<a href="https://www.bilibili.com/video/BV1Qv41167ck?p=55&amp;spm_id_from=pageDriver&amp;vd_source=2365b1baf9eff4a0af3aabdb705341bd" target="_blank" rel="noreferrer">Kubernetes(K8S) \u5165\u95E8\u8FDB\u9636\u5B9E\u6218\u5B8C\u6574\u6559\u7A0B\uFF0C\u9ED1\u9A6C\u7A0B\u5E8F\u5458K8S\u5168\u5957\u6559\u7A0B\uFF08\u57FA\u7840+\u9AD8\u7EA7\uFF09</a></p><h1 id="kubernetes\u4ECB\u7ECD" tabindex="-1">Kubernetes\u4ECB\u7ECD <a class="header-anchor" href="#kubernetes\u4ECB\u7ECD" aria-hidden="true">#</a></h1><h2 id="\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8" tabindex="-1">\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8 <a class="header-anchor" href="#\u4E00\u3001\u5E94\u7528\u90E8\u7F72\u65B9\u5F0F\u6F14\u53D8" aria-hidden="true">#</a></h2><p>\u5728\u90E8\u7F72\u5E94\u7528\u7A0B\u5E8F\u7684\u65B9\u5F0F\u4E0A\uFF0C\u4E3B\u8981\u7ECF\u5386\u4E86\u4E09\u4E2A\u65F6\u4EE3\uFF1A</p><ul><li><p><strong>\u4F20\u7EDF\u90E8\u7F72</strong>\uFF1A\u4E92\u8054\u7F51\u65E9\u671F\uFF0C\u4F1A\u76F4\u63A5\u5C06\u5E94\u7528\u7A0B\u5E8F\u90E8\u7F72\u5728\u7269\u7406\u673A\u4E0A</p><blockquote><p>\u4F18\u70B9\uFF1A\u7B80\u5355\uFF0C\u4E0D\u9700\u8981\u5176\u5B83\u6280\u672F\u7684\u53C2\u4E0E</p><p>\u7F3A\u70B9\uFF1A\u4E0D\u80FD\u4E3A\u5E94\u7528\u7A0B\u5E8F\u5B9A\u4E49\u8D44\u6E90\u4F7F\u7528\u8FB9\u754C\uFF0C\u5F88\u96BE\u5408\u7406\u5730\u5206\u914D\u8BA1\u7B97\u8D44\u6E90\uFF0C\u800C\u4E14\u7A0B\u5E8F\u4E4B\u95F4\u5BB9\u6613\u4EA7\u751F\u5F71\u54CD</p></blockquote></li><li><p><strong>\u865A\u62DF\u5316\u90E8\u7F72</strong>\uFF1A\u53EF\u4EE5\u5728\u4E00\u53F0\u7269\u7406\u673A\u4E0A\u8FD0\u884C\u591A\u4E2A\u865A\u62DF\u673A\uFF0C\u6BCF\u4E2A\u865A\u62DF\u673A\u90FD\u662F\u72EC\u7ACB\u7684\u4E00\u4E2A\u73AF\u5883</p><blockquote><p>\u4F18\u70B9\uFF1A\u7A0B\u5E8F\u73AF\u5883\u4E0D\u4F1A\u76F8\u4E92\u4EA7\u751F\u5F71\u54CD\uFF0C\u63D0\u4F9B\u4E86\u4E00\u5B9A\u7A0B\u5EA6\u7684\u5B89\u5168\u6027</p><p>\u7F3A\u70B9\uFF1A\u589E\u52A0\u4E86\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u6D6A\u8D39\u4E86\u90E8\u5206\u8D44\u6E90</p></blockquote></li><li><p><strong>\u5BB9\u5668\u5316\u90E8\u7F72</strong>\uFF1A\u4E0E\u865A\u62DF\u5316\u7C7B\u4F3C\uFF0C\u4F46\u662F\u5171\u4EAB\u4E86\u64CD\u4F5C\u7CFB\u7EDF</p><blockquote><p>\u4F18\u70B9\uFF1A</p><p>\u53EF\u4EE5\u4FDD\u8BC1\u6BCF\u4E2A\u5BB9\u5668\u62E5\u6709\u81EA\u5DF1\u7684\u6587\u4EF6\u7CFB\u7EDF\u3001CPU\u3001\u5185\u5B58\u3001\u8FDB\u7A0B\u7A7A\u95F4\u7B49</p><p>\u8FD0\u884C\u5E94\u7528\u7A0B\u5E8F\u6240\u9700\u8981\u7684\u8D44\u6E90\u90FD\u88AB\u5BB9\u5668\u5305\u88C5\uFF0C\u5E76\u548C\u5E95\u5C42\u57FA\u7840\u67B6\u6784\u89E3\u8026</p><p>\u5BB9\u5668\u5316\u7684\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u8DE8\u4E91\u670D\u52A1\u5546\u3001\u8DE8Linux\u64CD\u4F5C\u7CFB\u7EDF\u53D1\u884C\u7248\u8FDB\u884C\u90E8\u7F72</p></blockquote></li></ul><p><img src="'+o+'" alt="image-20200505183738289"></p><p>\u5BB9\u5668\u5316\u90E8\u7F72\u65B9\u5F0F\u7ED9\u5E26\u6765\u5F88\u591A\u7684\u4FBF\u5229\uFF0C\u4F46\u662F\u4E5F\u4F1A\u51FA\u73B0\u4E00\u4E9B\u95EE\u9898\uFF0C\u6BD4\u5982\u8BF4\uFF1A</p><ul><li>\u4E00\u4E2A\u5BB9\u5668\u6545\u969C\u505C\u673A\u4E86\uFF0C\u600E\u4E48\u6837\u8BA9\u53E6\u5916\u4E00\u4E2A\u5BB9\u5668\u7ACB\u523B\u542F\u52A8\u53BB\u66FF\u8865\u505C\u673A\u7684\u5BB9\u5668</li><li>\u5F53\u5E76\u53D1\u8BBF\u95EE\u91CF\u53D8\u5927\u7684\u65F6\u5019\uFF0C\u600E\u4E48\u6837\u505A\u5230\u6A2A\u5411\u6269\u5C55\u5BB9\u5668\u6570\u91CF</li></ul><p>\u8FD9\u4E9B\u5BB9\u5668\u7BA1\u7406\u7684\u95EE\u9898\u7EDF\u79F0\u4E3A<strong>\u5BB9\u5668\u7F16\u6392</strong>\u95EE\u9898\uFF0C\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E9B\u5BB9\u5668\u7F16\u6392\u95EE\u9898\uFF0C\u5C31\u4EA7\u751F\u4E86\u4E00\u4E9B\u5BB9\u5668\u7F16\u6392\u7684\u8F6F\u4EF6\uFF1A</p><ul><li><strong>Swarm</strong>\uFF1ADocker\u81EA\u5DF1\u7684\u5BB9\u5668\u7F16\u6392\u5DE5\u5177</li><li><strong>Mesos</strong>\uFF1AApache\u7684\u4E00\u4E2A\u8D44\u6E90\u7EDF\u4E00\u7BA1\u63A7\u7684\u5DE5\u5177\uFF0C\u9700\u8981\u548CMarathon\u7ED3\u5408\u4F7F\u7528</li><li><strong>Kubernetes</strong>\uFF1AGoogle\u5F00\u6E90\u7684\u7684\u5BB9\u5668\u7F16\u6392\u5DE5\u5177</li></ul><p><img src="'+s+'" alt="image-20200524150339551"></p><h2 id="\u4E8C\u3001kubernetes\u7B80\u4ECB" tabindex="-1">\u4E8C\u3001kubernetes\u7B80\u4ECB <a class="header-anchor" href="#\u4E8C\u3001kubernetes\u7B80\u4ECB" aria-hidden="true">#</a></h2><p><img src="'+p+'" alt="image-20200406232838722"></p><p>kubernetes\uFF0C\u662F\u4E00\u4E2A\u5168\u65B0\u7684\u57FA\u4E8E\u5BB9\u5668\u6280\u672F\u7684\u5206\u5E03\u5F0F\u67B6\u6784\u9886\u5148\u65B9\u6848\uFF0C\u662F\u8C37\u6B4C\u4E25\u683C\u4FDD\u5BC6\u5341\u51E0\u5E74\u7684\u79D8\u5BC6\u6B66\u5668----Borg\u7CFB\u7EDF\u7684\u4E00\u4E2A\u5F00\u6E90\u7248\u672C\uFF0C\u4E8E2014\u5E749\u6708\u53D1\u5E03\u7B2C\u4E00\u4E2A\u7248\u672C\uFF0C2015\u5E747\u6708\u53D1\u5E03\u7B2C\u4E00\u4E2A\u6B63\u5F0F\u7248\u672C\u3002</p><p>kubernetes\u7684\u672C\u8D28\u662F<strong>\u4E00\u7EC4\u670D\u52A1\u5668\u96C6\u7FA4</strong>\uFF0C\u5B83\u53EF\u4EE5\u5728\u96C6\u7FA4\u7684\u6BCF\u4E2A\u8282\u70B9\u4E0A\u8FD0\u884C\u7279\u5B9A\u7684\u7A0B\u5E8F\uFF0C\u6765\u5BF9\u8282\u70B9\u4E2D\u7684\u5BB9\u5668\u8FDB\u884C\u7BA1\u7406\u3002\u76EE\u7684\u662F\u5B9E\u73B0\u8D44\u6E90\u7BA1\u7406\u7684\u81EA\u52A8\u5316\uFF0C\u4E3B\u8981\u63D0\u4F9B\u4E86\u5982\u4E0B\u7684\u4E3B\u8981\u529F\u80FD\uFF1A</p><ul><li><strong>\u81EA\u6211\u4FEE\u590D</strong>\uFF1A\u4E00\u65E6\u67D0\u4E00\u4E2A\u5BB9\u5668\u5D29\u6E83\uFF0C\u80FD\u591F\u57281\u79D2\u4E2D\u5DE6\u53F3\u8FC5\u901F\u542F\u52A8\u65B0\u7684\u5BB9\u5668</li><li><strong>\u5F39\u6027\u4F38\u7F29</strong>\uFF1A\u53EF\u4EE5\u6839\u636E\u9700\u8981\uFF0C\u81EA\u52A8\u5BF9\u96C6\u7FA4\u4E2D\u6B63\u5728\u8FD0\u884C\u7684\u5BB9\u5668\u6570\u91CF\u8FDB\u884C\u8C03\u6574</li><li><strong>\u670D\u52A1\u53D1\u73B0</strong>\uFF1A\u670D\u52A1\u53EF\u4EE5\u901A\u8FC7\u81EA\u52A8\u53D1\u73B0\u7684\u5F62\u5F0F\u627E\u5230\u5B83\u6240\u4F9D\u8D56\u7684\u670D\u52A1</li><li><strong>\u8D1F\u8F7D\u5747\u8861</strong>\uFF1A\u5982\u679C\u4E00\u4E2A\u670D\u52A1\u8D77\u52A8\u4E86\u591A\u4E2A\u5BB9\u5668\uFF0C\u80FD\u591F\u81EA\u52A8\u5B9E\u73B0\u8BF7\u6C42\u7684\u8D1F\u8F7D\u5747\u8861</li><li><strong>\u7248\u672C\u56DE\u9000</strong>\uFF1A\u5982\u679C\u53D1\u73B0\u65B0\u53D1\u5E03\u7684\u7A0B\u5E8F\u7248\u672C\u6709\u95EE\u9898\uFF0C\u53EF\u4EE5\u7ACB\u5373\u56DE\u9000\u5230\u539F\u6765\u7684\u7248\u672C</li><li><strong>\u5B58\u50A8\u7F16\u6392</strong>\uFF1A\u53EF\u4EE5\u6839\u636E\u5BB9\u5668\u81EA\u8EAB\u7684\u9700\u6C42\u81EA\u52A8\u521B\u5EFA\u5B58\u50A8\u5377</li></ul><p><img src="'+i+'" alt="image-20230328160622076"></p><h2 id="\u4E09\u3001kubernetes\u7EC4\u4EF6" tabindex="-1">\u4E09\u3001kubernetes\u7EC4\u4EF6 <a class="header-anchor" href="#\u4E09\u3001kubernetes\u7EC4\u4EF6" aria-hidden="true">#</a></h2><p>\u4E00\u4E2Akubernetes\u96C6\u7FA4\u4E3B\u8981\u662F\u7531<strong>\u63A7\u5236\u8282\u70B9(master)</strong>\u3001**\u5DE5\u4F5C\u8282\u70B9(node)**\u6784\u6210\uFF0C\u6BCF\u4E2A\u8282\u70B9\u4E0A\u90FD\u4F1A\u5B89\u88C5\u4E0D\u540C\u7684\u7EC4\u4EF6\u3002</p><p><strong>master\uFF1A\u96C6\u7FA4\u7684\u63A7\u5236\u5E73\u9762\uFF0C\u8D1F\u8D23\u96C6\u7FA4\u7684\u51B3\u7B56 ( \u7BA1\u7406 )</strong></p><blockquote><p><strong>ApiServer</strong> : \u8D44\u6E90\u64CD\u4F5C\u7684\u552F\u4E00\u5165\u53E3\uFF0C\u63A5\u6536\u7528\u6237\u8F93\u5165\u7684\u547D\u4EE4\uFF0C\u63D0\u4F9B\u8BA4\u8BC1\u3001\u6388\u6743\u3001API\u6CE8\u518C\u548C\u53D1\u73B0\u7B49\u673A\u5236</p><p><strong>Scheduler</strong> : \u8D1F\u8D23\u96C6\u7FA4\u8D44\u6E90\u8C03\u5EA6\uFF0C\u6309\u7167\u9884\u5B9A\u7684\u8C03\u5EA6\u7B56\u7565\u5C06Pod\u8C03\u5EA6\u5230\u76F8\u5E94\u7684node\u8282\u70B9\u4E0A</p><p><strong>ControllerManager</strong> : \u8D1F\u8D23\u7EF4\u62A4\u96C6\u7FA4\u7684\u72B6\u6001\uFF0C\u6BD4\u5982\u7A0B\u5E8F\u90E8\u7F72\u5B89\u6392\u3001\u6545\u969C\u68C0\u6D4B\u3001\u81EA\u52A8\u6269\u5C55\u3001\u6EDA\u52A8\u66F4\u65B0\u7B49</p><p><strong>Etcd</strong> \uFF1A\u8D1F\u8D23\u5B58\u50A8\u96C6\u7FA4\u4E2D\u5404\u79CD\u8D44\u6E90\u5BF9\u8C61\u7684\u4FE1\u606F</p></blockquote><p><strong>node\uFF1A\u96C6\u7FA4\u7684\u6570\u636E\u5E73\u9762\uFF0C\u8D1F\u8D23\u4E3A\u5BB9\u5668\u63D0\u4F9B\u8FD0\u884C\u73AF\u5883 ( \u5E72\u6D3B )</strong></p><blockquote><p><strong>Kubelet</strong> : \u8D1F\u8D23\u7EF4\u62A4\u5BB9\u5668\u7684\u751F\u547D\u5468\u671F\uFF0C\u5373\u901A\u8FC7\u63A7\u5236docker\uFF0C\u6765\u521B\u5EFA\u3001\u66F4\u65B0\u3001\u9500\u6BC1\u5BB9\u5668</p><p><strong>KubeProxy</strong> : \u8D1F\u8D23\u63D0\u4F9B\u96C6\u7FA4\u5185\u90E8\u7684\u670D\u52A1\u53D1\u73B0\u548C\u8D1F\u8F7D\u5747\u8861</p><p><strong>Docker</strong> : \u8D1F\u8D23\u8282\u70B9\u4E0A\u5BB9\u5668\u7684\u5404\u79CD\u64CD\u4F5C</p></blockquote><p><img src="'+l+'" alt="image-20200406184656917"></p><p>\u4E0B\u9762\uFF0C\u4EE5\u90E8\u7F72\u4E00\u4E2Anginx\u670D\u52A1\u6765\u8BF4\u660Ekubernetes\u7CFB\u7EDF\u5404\u4E2A\u7EC4\u4EF6\u8C03\u7528\u5173\u7CFB\uFF1A</p><ol><li><p>\u9996\u5148\u8981\u660E\u786E\uFF0C\u4E00\u65E6kubernetes\u73AF\u5883\u542F\u52A8\u4E4B\u540E\uFF0Cmaster\u548Cnode\u90FD\u4F1A\u5C06\u81EA\u8EAB\u7684\u4FE1\u606F\u5B58\u50A8\u5230etcd\u6570\u636E\u5E93\u4E2D</p></li><li><p>\u4E00\u4E2Anginx\u670D\u52A1\u7684\u5B89\u88C5\u8BF7\u6C42\u4F1A\u9996\u5148\u88AB\u53D1\u9001\u5230master\u8282\u70B9\u7684apiServer\u7EC4\u4EF6</p></li><li><p>apiServer\u7EC4\u4EF6\u4F1A\u8C03\u7528scheduler\u7EC4\u4EF6\u6765\u51B3\u5B9A\u5230\u5E95\u5E94\u8BE5\u628A\u8FD9\u4E2A\u670D\u52A1\u5B89\u88C5\u5230\u54EA\u4E2Anode\u8282\u70B9\u4E0A</p><p>\u5728\u6B64\u65F6\uFF0C\u5B83\u4F1A\u4ECEetcd\u4E2D\u8BFB\u53D6\u5404\u4E2Anode\u8282\u70B9\u7684\u4FE1\u606F\uFF0C\u7136\u540E\u6309\u7167\u4E00\u5B9A\u7684\u7B97\u6CD5\u8FDB\u884C\u9009\u62E9\uFF0C\u5E76\u5C06\u7ED3\u679C\u544A\u77E5apiServer</p></li><li><p>apiServer\u8C03\u7528controller-manager\u53BB\u8C03\u5EA6Node\u8282\u70B9\u5B89\u88C5nginx\u670D\u52A1</p></li><li><p>kubelet\u63A5\u6536\u5230\u6307\u4EE4\u540E\uFF0C\u4F1A\u901A\u77E5docker\uFF0C\u7136\u540E\u7531docker\u6765\u542F\u52A8\u4E00\u4E2Anginx\u7684pod</p><p>pod\u662Fkubernetes\u7684\u6700\u5C0F\u64CD\u4F5C\u5355\u5143\uFF0C\u5BB9\u5668\u5FC5\u987B\u8DD1\u5728pod\u4E2D\u81F3\u6B64\uFF0C</p></li><li><p>\u4E00\u4E2Anginx\u670D\u52A1\u5C31\u8FD0\u884C\u4E86\uFF0C\u5982\u679C\u9700\u8981\u8BBF\u95EEnginx\uFF0C\u5C31\u9700\u8981\u901A\u8FC7kube-proxy\u6765\u5BF9pod\u4EA7\u751F\u8BBF\u95EE\u7684\u4EE3\u7406</p></li></ol><p>\u8FD9\u6837\uFF0C\u5916\u754C\u7528\u6237\u5C31\u53EF\u4EE5\u8BBF\u95EE\u96C6\u7FA4\u4E2D\u7684nginx\u670D\u52A1\u4E86</p><h2 id="\u56DB\u3001kubernetes\u6982\u5FF5" tabindex="-1">\u56DB\u3001kubernetes\u6982\u5FF5 <a class="header-anchor" href="#\u56DB\u3001kubernetes\u6982\u5FF5" aria-hidden="true">#</a></h2><p><strong>Master</strong>\uFF1A\u96C6\u7FA4\u63A7\u5236\u8282\u70B9\uFF0C\u6BCF\u4E2A\u96C6\u7FA4\u9700\u8981\u81F3\u5C11\u4E00\u4E2Amaster\u8282\u70B9\u8D1F\u8D23\u96C6\u7FA4\u7684\u7BA1\u63A7</p><p><strong>Node</strong>\uFF1A\u5DE5\u4F5C\u8D1F\u8F7D\u8282\u70B9\uFF0C\u7531master\u5206\u914D\u5BB9\u5668\u5230\u8FD9\u4E9Bnode\u5DE5\u4F5C\u8282\u70B9\u4E0A\uFF0C\u7136\u540Enode\u8282\u70B9\u4E0A\u7684docker\u8D1F\u8D23\u5BB9\u5668\u7684\u8FD0\u884C</p><p><strong>Pod</strong>\uFF1Akubernetes\u7684\u6700\u5C0F\u63A7\u5236\u5355\u5143\uFF0C\u5BB9\u5668\u90FD\u662F\u8FD0\u884C\u5728pod\u4E2D\u7684\uFF0C\u4E00\u4E2Apod\u4E2D\u53EF\u4EE5\u67091\u4E2A\u6216\u8005\u591A\u4E2A\u5BB9\u5668</p><p><strong>Controller</strong>\uFF1A\u63A7\u5236\u5668\uFF0C\u901A\u8FC7\u5B83\u6765\u5B9E\u73B0\u5BF9pod\u7684\u7BA1\u7406\uFF0C\u6BD4\u5982\u542F\u52A8pod\u3001\u505C\u6B62pod\u3001\u4F38\u7F29pod\u7684\u6570\u91CF\u7B49\u7B49</p><p><strong>Service</strong>\uFF1Apod\u5BF9\u5916\u670D\u52A1\u7684\u7EDF\u4E00\u5165\u53E3\uFF0C\u4E0B\u9762\u53EF\u4EE5\u7EF4\u62A4\u8005\u540C\u4E00\u7C7B\u7684\u591A\u4E2Apod</p><p><strong>Label</strong>\uFF1A\u6807\u7B7E\uFF0C\u7528\u4E8E\u5BF9pod\u8FDB\u884C\u5206\u7C7B\uFF0C\u540C\u4E00\u7C7Bpod\u4F1A\u62E5\u6709\u76F8\u540C\u7684\u6807\u7B7E</p><p><strong>NameSpace</strong>\uFF1A\u547D\u540D\u7A7A\u95F4\uFF0C\u7528\u6765\u9694\u79BBpod\u7684\u8FD0\u884C\u73AF\u5883</p>',35),u=[g];function d(c,b,k,h,m,_){return t(),r("div",null,u)}const v=e(a,[["render",d]]);export{x as __pageData,v as default};
