import{_ as e,c as d,o,d as c}from"./app.77380161.js";const R=JSON.parse('{"title":"Redis\u4ECB\u7ECD","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7B80\u4ECB","slug":"\u7B80\u4ECB","link":"#\u7B80\u4ECB","children":[]},{"level":2,"title":"\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0redis","slug":"\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0redis","link":"#\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0redis","children":[]},{"level":2,"title":"redis\u7684\u7279\u6027","slug":"redis\u7684\u7279\u6027","link":"#redis\u7684\u7279\u6027","children":[]}],"relativePath":"classify/redis/redis\u4E8B\u52A1.md","lastUpdated":1665507256000}'),i={name:"classify/redis/redis\u4E8B\u52A1.md"},s=c('<h1 id="redis\u4ECB\u7ECD" tabindex="-1">Redis\u4ECB\u7ECD <a class="header-anchor" href="#redis\u4ECB\u7ECD" aria-hidden="true">#</a></h1><h2 id="\u7B80\u4ECB" tabindex="-1">\u7B80\u4ECB <a class="header-anchor" href="#\u7B80\u4ECB" aria-hidden="true">#</a></h2><p><strong><code>redis</code>\u662F\u4E00\u4E2A<code>key-value</code>\u5B58\u50A8\u7CFB\u7EDF</strong>\u3002\u5B83\u652F\u6301\u5B58\u50A8\u7684<code>value</code>\u7C7B\u578B\u76F8\u5BF9\u66F4\u591A\uFF0C\u5305\u62EC**<code>string</code>(\u5B57\u7B26\u4E32)\u3001<code>list</code>(\u94FE\u8868)\u3001<code>set</code>(\u96C6\u5408)\u3001<code>zset</code>(<code>sorted set</code> -- \u6709\u5E8F\u96C6\u5408)\u548C <code>hash</code>\uFF08\u54C8\u5E0C\u7C7B\u578B\uFF09**\u3002\u8FD9\u4E9B\u6570\u636E\u7C7B\u578B\u90FD\u652F\u6301<code>push/pop</code>\u3001 <code>add/remove</code>\u53CA\u53D6\u4EA4\u96C6\u5E76\u96C6\u548C\u5DEE\u96C6\u53CA\u66F4\u4E30\u5BCC\u7684\u64CD\u4F5C\uFF0C\u800C\u4E14\u8FD9\u4E9B\u64CD\u4F5C\u90FD\u662F\u539F\u5B50\u6027\u7684\u3002\u5728\u6B64\u57FA\u7840\u4E0A\uFF0C<code>redis</code>\u652F\u6301\u5404\u79CD\u4E0D\u540C\u65B9\u5F0F\u7684\u6392\u5E8F\u3002<strong>\u4E3A\u4E86\u4FDD\u8BC1\u6548\u7387\uFF0C\u6570\u636E\u90FD\u662F\u7F13\u5B58\u5728\u5185\u5B58\u4E2D \u3002</strong></p><p>\u4E66\u7C4D\u53C2\u8003\uFF1A\u300A<code>Redis</code> \u8BBE\u8BA1\u4E0E\u5B9E\u73B0\u300B<a href="http://redisbook.com/index.html#" target="_blank" rel="noreferrer">http://redisbook.com/index.html#</a></p><h2 id="\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0redis" tabindex="-1">\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0<code>redis</code> <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0redis" aria-hidden="true">#</a></h2><p>\u5BF9\u4E8E\u7F51\u9875\u6765\u8BF4\u6570\u636E\u6765\u81EA\u4E8E\u6570\u636E\u5E93\uFF0C\u800C\u6570\u636E\u5E93\u5B9E\u9645\u4E0A\u662F\u5B58\u5728\u8FD9\u6027\u80FD\u7684\u74F6\u9888\uFF08\u4E00\u822C\u5355\u8868\u65E9\u671F\u7684\u74F6\u9888\u5C31\u5728<code>2000w</code>\u4E86--[\u7D22\u5F15\u7684\u4F18\u5316\u6548\u7387\u74F6\u9888]\uFF09</p><p>\u5373\u4F7F\u662F\u505A\u4E86\u4E3B\u4ECE\u4EE5\u53CA\u96C6\u7FA4\u67B6\u6784\u4E5F\u4F9D\u7136\u8FD8\u662F\u4F1A\u6709\u901F\u7387\u7684\u95EE\u9898\uFF0C\u56E0\u6B64\u8FD9\u4E2A\u65F6\u5019\u4EE5\u5185\u5B58\u6682\u65F6\u5B58\u50A8\u6570\u636E\u7684\u60F3\u6CD5\u5C31\u6A2A\u7A7A\u51FA\u4E16\u4E86\u3002</p><h2 id="redis\u7684\u7279\u6027" tabindex="-1"><strong><code>redis</code>\u7684\u7279\u6027</strong> <a class="header-anchor" href="#redis\u7684\u7279\u6027" aria-hidden="true">#</a></h2><p>\u4E00\u3001\u901F\u5EA6\u5FEB</p><p>\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF0C<code>Redis</code>\u6267\u884C\u547D\u4EE4\u7684\u901F\u5EA6\u975E\u5E38\u5FEB\uFF0C\u5B98\u65B9\u7ED9\u51FA\u7684\u6570\u5B57\u662F<strong>\u8BFB\u5199\u6027\u80FD\u53EF\u4EE5\u8FBE\u523010\u4E07/\u79D2\uFF08\u8BFB11\u4E07\u6B21/\u79D2\uFF0C\u51998\u4E07\u6B21/\u79D2\uFF09</strong>\uFF0C\u5F53\u7136\u8FD9\u4E5F\u53D6\u51B3\u4E8E\u673A\u5668\u7684\u6027\u80FD\uFF0C\u4F46\u8FD9\u91CC\u5148\u4E0D\u8BA8\u8BBA\u673A\u5668\u6027\u80FD\u4E0A\u7684\u5DEE\u5F02\uFF0C\u53EA\u5206\u6790\u4E00\u4E0B\u662F\u4EC0\u4E48\u9020\u5C31\u4E86<code>Redis</code>\u9664\u6B64\u4E4B\u5FEB\u7684\u901F\u5EA6\uFF0C\u53EF\u4EE5\u5927\u81F4\u5F52\u7EB3\u4E3A\u4EE5\u4E0B\u56DB\u70B9\uFF1A</p><ol><li><code>Redis</code>\u7684<strong>\u6240\u6709\u6570\u636E\u90FD\u662F\u5B58\u653E\u5728\u5185\u5B58\u4E2D\u7684</strong>\uFF0C\u6240\u4EE5\u628A<strong>\u6570\u636E\u653E\u5728\u5185\u5B58\u4E2D\u662F<code>Redis</code>\u901F\u5EA6\u5FEB</strong>\u7684\u6700\u4E3B\u8981\u539F\u56E0\u3002</li><li><code>Redis</code>\u662F\u7528**<code>C</code>\u8BED\u8A00\u5B9E\u73B0\u7684\uFF0C\u4E00\u822C\u6765\u8BF4<code>C</code>\u8BED\u8A00\u5B9E\u73B0\u7684\u7A0B\u5E8F\u201C\u8DDD\u79BB\u201D\u64CD\u4F5C\u7CFB\u7EDF\u66F4\u8FD1\uFF0C\u6267\u884C\u901F\u5EA6\u76F8\u5BF9\u4F1A\u66F4\u5FEB**\uFF0C\u65E0\u9700\u50CF<code>php</code>\u8981\u5148\u89E3\u6790\u6210<code>opcode</code>\u518D\u7F16\u8BD1\u6210<code>machine code</code>\u3002</li><li><strong><code>Redis</code>\u4F7F\u7528\u4E86\u5355\u7EBF\u7A0B\u67B6\u6784\uFF0C\u9884\u9632\u4E86\u591A\u7EBF\u7A0B\u53EF\u80FD\u4EA7\u751F\u7684\u7ADE\u4E89\u95EE\u9898</strong>\u3002</li><li>\u4F5C\u8005\u5BF9\u4E8E<code>Redis</code>\u6E90\u4EE3\u7801\u53EF\u4EE5\u8BF4\u662F\u7CBE\u6253\u7EC6\u78E8\uFF0C\u66FE\u7ECF\u6709\u4EBA\u8BC4\u4EF7<code>Redis</code>\u662F\u5C11\u6709\u7684\u96C6\u6027\u80FD\u548C\u4F18\u96C5\u4E8E\u4E00\u8EAB\u7684\u5F00\u6E90\u4EE3\u7801\u3002</li></ol><p>\u4E8C\u3001\u57FA\u4E8E<code>key-value </code></p><p>\u51E0\u4E4E\u6240\u6709\u7684\u7F16\u7A0B\u8BED\u8A00\u90FD\u63D0\u4F9B\u4E86\u7C7B\u4F3C\u5B57\u5178\u7684\u529F\u80FD\uFF0C\u4F8B\u5982<code>Java</code>\u91CC\u7684<code>map</code>\u3001<code>Python</code>\u91CC\u7684<code>dict</code>\uFF0C\u7C7B\u4F3C\u4E8E\u8FD9\u79CD\u7EC4\u7EC7\u6570\u636E\u7684\u65B9\u5F0F\u53EB\u4F5C\u57FA\u4E8E\u952E\u503C\u7684\u65B9\u5F0F\uFF0C\u4E0E\u5F88\u591A\u952E\u503C\u5BF9\u6570\u636E\u5E93\u4E0D\u540C\u7684\u662F\uFF0C<code>Redis</code> \u4E2D\u7684\u503C\u4E0D\u4EC5\u53EF\u4EE5\u662F\u5B57\u7B26\u4E32\uFF0C\u800C\u4E14\u8FD8\u53EF\u4EE5\u662F\u5177\u4F53\u7684\u6570\u636E\u7ED3\u6784\uFF0C\u8FD9\u6837\u4E0D\u4EC5\u80FD\u4FBF\u4E8E\u5728\u8BB8\u591A\u5E94\u7528\u573A\u666F\u7684\u5F00\u53D1\uFF0C\u540C\u65F6\u4E5F\u80FD\u591F\u63D0\u9AD8\u5F00\u53D1\u6548\u7387\u3002</p><p><code>Redis</code> \u7684\u5168\u79F0\u662F<code>REmote Dictionary Server</code>\uFF0C\u5B83\u4E3B\u8981\u63D0\u4F9B\u4E865\u79CD\u6570\u636E\u7ED3\u6784\uFF1A\u5B57\u7B26\u4E32\u3001\u54C8\u5E0C\u3001\u5217\u8868\u3001\u96C6\u5408\u3001\u6709\u5E8F\u96C6\u5408\uFF0C\u540C\u65F6\u5728\u5B57\u7B26\u4E32\u7684\u57FA\u7840\u4E4B\u4E0A\u6F14\u53D8\u51FA\u4E86\u4F4D\u56FE( <code>Bitmaps</code>)\u548C<code>HyperLogLog</code>\u4E24\u79CD\u795E\u5947\u7684\u201C\u6570\u636E\u7ED3\u6784\u201D\uFF0C\u5E76\u4E14\u968F\u7740<code>LBS</code> ( <code>Location BasedService</code>\uFF0C\u57FA\u4E8E\u4F4D\u7F6E\u670D\u52A1)\u7684\u4E0D\u65AD\u53D1\u5C55\uFF0C<code>Redis 3.2</code>\u7248\u672C\u4E2D\u52A0\u4EBA\u6709\u5173<code>GEO</code> (\u5730\u7406\u4FE1\u606F\u5B9A\u4F4D)\u7684\u529F\u80FD\uFF0C\u603B\u4E4B\u5728\u8FD9\u4E9B\u6570\u636E\u7ED3\u6784\u7684\u5E2E\u52A9\u4E0B\uFF0C\u5F00\u53D1\u8005\u53EF\u4EE5\u5F00\u53D1\u51FA\u5404\u79CD\u201C\u6709\u610F\u601D\u201D\u7684\u5E94\u7528\u3002</p><p>\u4E09\u3001\u4E30\u5BCC\u7684\u529F\u80FD</p><p>\u9664\u4E865\u79CD\u6570\u636E\u7ED3\u6784\uFF0C<code>Redis</code>\u8FD8\u63D0\u4F9B\u4E86\u8BB8\u591A\u989D\u5916\u7684\u529F\u80FD:</p><ol><li>\u63D0\u4F9B\u4E86\u952E\u8FC7\u671F\u529F\u80FD\uFF0C\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u7F13\u5B58\u3002</li><li>\u4F9B\u4E86\u53D1\u5E03\u8BA2\u9605\u529F\u80FD\uFF0C\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u6D88\u606F\u7CFB\u7EDF\u3002</li><li>\u652F\u6301<code>Lua</code>\u811A\u672C\u529F\u80FD\uFF0C\u53EF\u4EE5\u5229\u7528<code>Lua</code>\u521B\u9020\u51FA\u65B0\u7684<code>Redis</code>\u547D\u4EE4\u3002</li><li>\u63D0\u4F9B\u4E86\u7B80\u5355\u7684\u4E8B\u52A1\u529F\u80FD\uFF0C\u80FD\u5728\u4E00\u5B9A\u7A0B\u5EA6 \u4E0A\u4FDD\u8BC1\u4E8B\u52A1\u7279\u6027\u3002</li><li>\u63D0\u4F9B\u4E86\u6D41\u6C34\u7EBF( <code>Pipeline</code>)\u529F\u80FD\uFF0C\u8FD9\u6837\u5BA2\u6237\u7AEF\u80FD\u5C06-\u6279\u547D\u4EE4-\u4E00\u6B21\u6027\u4F20\u5230<code>Redis</code>\uFF0C\u51CF\u5C11\u7F51\u7EDC\u7684\u5F00\u9500\u3002</li></ol>',17),r=[s];function t(a,l,n,p,h,_){return o(),d("div",null,r)}const u=e(i,[["render",t]]);export{R as __pageData,u as default};
