import{_ as e,c as t,o as r,a as p}from"./app.867b012f.js";const _=JSON.parse('{"title":"\u6027\u80FD\u6307\u6807","description":"","frontmatter":{},"headers":[{"level":2,"title":"RT - \u54CD\u5E94\u65F6\u95F4","slug":"rt-\u54CD\u5E94\u65F6\u95F4","link":"#rt-\u54CD\u5E94\u65F6\u95F4","children":[]},{"level":2,"title":"TPS - \u541E\u5410\u91CF  (Throughput)","slug":"tps-\u541E\u5410\u91CF-throughput","link":"#tps-\u541E\u5410\u91CF-throughput","children":[]},{"level":2,"title":"QPS - \u6BCF\u79D2\u67E5\u8BE2\u7387  (Query Per Second)","slug":"qps-\u6BCF\u79D2\u67E5\u8BE2\u7387-query-per-second","link":"#qps-\u6BCF\u79D2\u67E5\u8BE2\u7387-query-per-second","children":[]},{"level":2,"title":"PV - \u8BBF\u95EE\u91CF (Page View)","slug":"pv-\u8BBF\u95EE\u91CF-page-view","link":"#pv-\u8BBF\u95EE\u91CF-page-view","children":[]},{"level":2,"title":"UV - \u72EC\u7ACB\u8BBF\u5BA2 (Unique Visitor)","slug":"uv-\u72EC\u7ACB\u8BBF\u5BA2-unique-visitor","link":"#uv-\u72EC\u7ACB\u8BBF\u5BA2-unique-visitor","children":[]},{"level":2,"title":"IV","slug":"iv","link":"#iv","children":[]},{"level":2,"title":"\u5E26\u5BBD","slug":"\u5E26\u5BBD","link":"#\u5E26\u5BBD","children":[]},{"level":2,"title":"\u5E76\u53D1\u7528\u6237\u6570","slug":"\u5E76\u53D1\u7528\u6237\u6570","link":"#\u5E76\u53D1\u7528\u6237\u6570","children":[]}],"relativePath":"classify/sundry/concept/\u6027\u80FD\u6307\u6807.md","lastUpdated":1680256036000}'),a={name:"classify/sundry/concept/\u6027\u80FD\u6307\u6807.md"},i=p('<h1 id="\u6027\u80FD\u6307\u6807" tabindex="-1">\u6027\u80FD\u6307\u6807 <a class="header-anchor" href="#\u6027\u80FD\u6307\u6807" aria-hidden="true">#</a></h1><h2 id="rt-\u54CD\u5E94\u65F6\u95F4" tabindex="-1">RT - \u54CD\u5E94\u65F6\u95F4 <a class="header-anchor" href="#rt-\u54CD\u5E94\u65F6\u95F4" aria-hidden="true">#</a></h2><p>\u54CD\u5E94\u65F6\u95F4\u662F\u6307\u7CFB\u7EDF\u5BF9\u8BF7\u6C42\u4F5C\u51FA\u54CD\u5E94\u7684\u65F6\u95F4\u3002</p><p>\u76F4\u89C2\u4E0A\u770B\uFF0C\u8FD9\u4E2A\u6307\u6807\u4E0E\u4EBA\u5BF9\u8F6F\u4EF6\u6027\u80FD\u7684\u4E3B\u89C2\u611F\u53D7\u662F\u975E\u5E38\u4E00\u81F4\u7684\uFF0C\u56E0\u4E3A\u5B83\u5B8C\u6574\u5730\u8BB0\u5F55\u4E86\u6574\u4E2A\u8BA1\u7B97\u673A\u7CFB\u7EDF\u5904\u7406\u8BF7\u6C42\u7684\u65F6\u95F4\u3002\u7531\u4E8E\u4E00\u4E2A\u7CFB\u7EDF\u901A\u5E38\u4F1A\u63D0\u4F9B\u8BB8\u591A\u529F\u80FD\uFF0C\u800C\u4E0D\u540C\u529F\u80FD\u7684\u5904\u7406\u903B\u8F91\u4E5F\u5343\u5DEE\u4E07\u522B\uFF0C\u56E0\u800C\u4E0D\u540C\u529F\u80FD\u7684\u54CD\u5E94\u65F6\u95F4\u4E5F\u4E0D\u5C3D\u76F8\u540C\uFF0C\u751A\u81F3\u540C\u4E00\u529F\u80FD\u5728\u4E0D\u540C\u8F93\u5165\u6570\u636E\u7684\u60C5\u51B5\u4E0B\u54CD\u5E94\u65F6\u95F4\u4E5F\u4E0D\u76F8\u540C\u3002\u6240\u4EE5\uFF0C\u5728\u8BA8\u8BBA\u4E00\u4E2A\u7CFB\u7EDF\u7684\u54CD\u5E94\u65F6\u95F4\u65F6\uFF0C\u4EBA\u4EEC\u901A\u5E38\u662F\u6307\u8BE5\u7CFB\u7EDF\u6240\u6709\u529F\u80FD\u7684\u5E73\u5747\u65F6\u95F4\u6216\u8005\u6240\u6709\u529F\u80FD\u7684\u6700\u5927\u54CD\u5E94\u65F6\u95F4\u3002\u5F53\u7136\uFF0C\u5F80\u5F80\u4E5F\u9700\u8981\u5BF9\u6BCF\u4E2A\u6216\u6BCF\u7EC4\u529F\u80FD\u8BA8\u8BBA\u5176\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u548C\u6700\u5927\u54CD\u5E94\u65F6\u95F4\u3002</p><p>\u5BF9\u4E8E\u5355\u673A\u7684\u6CA1\u6709\u5E76\u53D1\u64CD\u4F5C\u7684\u5E94\u7528\u7CFB\u7EDF\u800C\u8A00\uFF0C\u4EBA\u4EEC\u666E\u904D\u8BA4\u4E3A\u54CD\u5E94\u65F6\u95F4\u662F\u4E00\u4E2A\u5408\u7406\u4E14\u51C6\u786E\u7684\u6027\u80FD\u6307\u6807\u3002\u9700\u8981\u6307\u51FA\u7684\u662F\uFF0C\u54CD\u5E94\u65F6\u95F4\u7684\u7EDD\u5BF9\u503C\u5E76\u4E0D\u80FD\u76F4\u63A5\u53CD\u6620\u8F6F\u4EF6\u7684\u6027\u80FD\u7684\u9AD8\u4F4E\uFF0C\u8F6F\u4EF6\u6027\u80FD\u7684\u9AD8\u4F4E\u5B9E\u9645\u4E0A\u53D6\u51B3\u4E8E\u7528\u6237\u5BF9\u8BE5\u54CD\u5E94\u65F6\u95F4\u7684\u63A5\u53D7\u7A0B\u5EA6\u3002\u5BF9\u4E8E\u4E00\u4E2A\u6E38\u620F\u8F6F\u4EF6\u6765\u8BF4\uFF0C\u54CD\u5E94\u65F6\u95F4\u5C0F\u4E8E100\u6BEB\u79D2\u5E94\u8BE5\u662F\u4E0D\u9519\u7684\uFF0C\u54CD\u5E94\u65F6\u95F4\u57281\u79D2\u5DE6\u53F3\u53EF\u80FD\u5C5E\u4E8E\u52C9\u5F3A\u53EF\u4EE5\u63A5\u53D7\uFF0C\u5982\u679C\u54CD\u5E94\u65F6\u95F4\u8FBE\u52303\u79D2\u5C31\u5B8C\u5168\u96BE\u4EE5\u63A5\u53D7\u4E86\u3002\u800C\u5BF9\u4E8E\u7F16\u8BD1\u7CFB\u7EDF\u6765\u8BF4\uFF0C\u5B8C\u6574\u7F16\u8BD1\u4E00\u4E2A\u8F83\u5927\u89C4\u6A21\u8F6F\u4EF6\u7684\u6E90\u4EE3\u7801\u53EF\u80FD\u9700\u8981\u51E0\u5341\u5206\u949F\u751A\u81F3\u66F4\u957F\u65F6\u95F4\uFF0C\u4F46\u8FD9\u4E9B\u54CD\u5E94\u65F6\u95F4\u5BF9\u4E8E\u7528\u6237\u6765\u8BF4\u90FD\u662F\u53EF\u4EE5\u63A5\u53D7\u7684\u3002</p><h2 id="tps-\u541E\u5410\u91CF-throughput" tabindex="-1">TPS - \u541E\u5410\u91CF (Throughput) <a class="header-anchor" href="#tps-\u541E\u5410\u91CF-throughput" aria-hidden="true">#</a></h2><p>\u541E\u5410\u91CF\u662F\u6307\u7CFB\u7EDF\u5728\u5355\u4F4D\u65F6\u95F4\u5185\u5904\u7406\u8BF7\u6C42\u7684\u6570\u91CF\u3002</p><p><strong>\u89E3\u91CA\uFF1A</strong></p><p>\u5BF9\u4E8E\u65E0\u5E76\u53D1\u7684\u5E94\u7528\u7CFB\u7EDF\u800C\u8A00\uFF0C\u541E\u5410\u91CF\u4E0E\u54CD\u5E94\u65F6\u95F4\u6210\u4E25\u683C\u7684\u53CD\u6BD4\u5173\u7CFB\uFF0C\u5B9E\u9645\u4E0A\u6B64\u65F6\u541E\u5410\u91CF\u5C31\u662F\u54CD\u5E94\u65F6\u95F4\u7684\u5012\u6570\u3002\u524D\u9762\u5DF2\u7ECF\u8BF4\u8FC7\uFF0C\u5BF9\u4E8E\u5355\u7528\u6237\u7684\u7CFB\u7EDF\uFF0C\u54CD\u5E94\u65F6\u95F4\uFF08\u6216\u8005\u7CFB\u7EDF\u54CD\u5E94\u65F6\u95F4\u548C\u5E94\u7528\u5EF6\u8FDF\u65F6\u95F4\uFF09\u53EF\u4EE5\u5F88\u597D\u5730\u5EA6\u91CF\u7CFB\u7EDF\u7684\u6027\u80FD\uFF0C\u4F46\u5BF9\u4E8E\u5E76\u53D1\u7CFB\u7EDF\uFF0C\u901A\u5E38\u9700\u8981\u7528\u541E\u5410\u91CF\u4F5C\u4E3A\u6027\u80FD\u6307\u6807\u3002</p><p>\u5BF9\u4E8E\u4E00\u4E2A\u591A\u7528\u6237\u7684\u7CFB\u7EDF\uFF0C\u5982\u679C\u53EA\u6709\u4E00\u4E2A\u7528\u6237\u4F7F\u7528\u65F6\u7CFB\u7EDF\u7684\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u662Ft\uFF0C\u5F53\u6709\u4F60n\u4E2A\u7528\u6237\u4F7F\u7528\u65F6\uFF0C\u6BCF\u4E2A\u7528\u6237\u770B\u5230\u7684\u54CD\u5E94\u65F6\u95F4\u901A\u5E38\u5E76\u4E0D\u662Fn\xD7t\uFF0C\u800C\u5F80\u5F80\u6BD4n\xD7t\u5C0F\u5F88\u591A\uFF08\u5F53\u7136\uFF0C\u5728\u67D0\u4E9B\u7279\u6B8A\u60C5\u51B5\u4E0B\u4E5F\u53EF\u80FD\u6BD4n\xD7t\u5927\uFF0C\u751A\u81F3\u5927\u5F88\u591A\uFF09\u3002\u8FD9\u662F\u56E0\u4E3A\u5904\u7406\u6BCF\u4E2A\u8BF7\u6C42\u9700\u8981\u7528\u5230\u5F88\u591A\u8D44\u6E90\uFF0C\u7531\u4E8E\u6BCF\u4E2A\u8BF7\u6C42\u7684\u5904\u7406\u8FC7\u7A0B\u4E2D\u6709\u8BB8\u591A\u4E0D\u8D70\u96BE\u4EE5\u5E76\u53D1\u6267\u884C\uFF0C\u8FD9\u5BFC\u81F4\u5728\u5177\u4F53\u7684\u4E00\u4E2A\u65F6\u95F4\u70B9\uFF0C\u6240\u5360\u8D44\u6E90\u5F80\u5F80\u5E76\u4E0D\u591A\u3002\u4E5F\u5C31\u662F\u8BF4\u5728\u5904\u7406\u5355\u4E2A\u8BF7\u6C42\u65F6\uFF0C\u5728\u6BCF\u4E2A\u65F6\u95F4\u70B9\u90FD\u53EF\u80FD\u6709\u8BB8\u591A\u8D44\u6E90\u88AB\u95F2\u7F6E\uFF0C\u5F53\u5904\u7406\u591A\u4E2A\u8BF7\u6C42\u65F6\uFF0C\u5982\u679C\u8D44\u6E90\u914D\u7F6E\u5408\u7406\uFF0C\u6BCF\u4E2A\u7528\u6237\u770B\u5230\u7684\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u5E76\u4E0D\u968F\u7528\u6237\u6570\u7684\u589E\u52A0\u800C\u7EBF\u6027\u589E\u52A0\u3002\u5B9E\u9645\u4E0A\uFF0C\u4E0D\u540C\u7CFB\u7EDF\u7684\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u968F\u7528\u6237\u6570\u589E\u52A0\u800C\u589E\u957F\u7684\u901F\u5EA6\u4E5F\u4E0D\u5927\u76F8\u540C\uFF0C\u8FD9\u4E5F\u662F\u91C7\u7528\u541E\u5410\u91CF\u6765\u5EA6\u91CF\u5E76\u53D1\u7CFB\u7EDF\u7684\u6027\u80FD\u7684\u4E3B\u8981\u539F\u56E0\u3002\u4E00\u822C\u800C\u8A00\uFF0C\u541E\u5410\u91CF\u662F\u4E00\u4E2A\u6BD4\u8F83\u901A\u7528\u7684\u6307\u6807\uFF0C\u4E24\u4E2A\u5177\u6709\u4E0D\u540C\u7528\u6237\u6570\u548C\u7528\u6237\u4F7F\u7528\u6A21\u5F0F\u7684\u7CFB\u7EDF\uFF0C\u5982\u679C\u5176\u6700\u5927\u541E\u5410\u91CF\u57FA\u672C\u4E00\u81F4\uFF0C\u5219\u53EF\u4EE5\u5224\u65AD\u4E24\u4E2A\u7CFB\u7EDF\u7684\u5904\u7406\u80FD\u529B\u57FA\u672C\u4E00\u81F4\u3002</p><h2 id="qps-\u6BCF\u79D2\u67E5\u8BE2\u7387-query-per-second" tabindex="-1">QPS - \u6BCF\u79D2\u67E5\u8BE2\u7387 (Query Per Second) <a class="header-anchor" href="#qps-\u6BCF\u79D2\u67E5\u8BE2\u7387-query-per-second" aria-hidden="true">#</a></h2><p>\u6BCF\u79D2\u67E5\u8BE2\u6570\uFF0C\u53EF\u4EE5\u770B\u6210\u662F\u6700\u5927\u541E\u5410\u91CF\u3002</p><p><strong>\u89E3\u91CA\uFF1A</strong></p><p>QPS \u5F88\u5927\u7A0B\u5EA6\u4E0A\u4EE3\u8868\u4E86\u7CFB\u7EDF\u4E1A\u52A1\u7684\u7E41\u5FD9\u7A0B\u5EA6\uFF0C\u6BCF\u6B21\u8BF7\u6C42\u7684\u80CC\u540E\uFF0C\u53EF\u80FD\u5BF9\u5E94\u7740\u591A\u6B21\u78C1\u76D8I/O\uFF0C\u591A\u6B21\u7F51\u7EDC\u8BF7\u6C42\uFF0C\u591A\u4E2ACPU\u65F6\u95F4\u7247\u7B49\u3002\u6211\u4EEC\u901A\u8FC7 QPS \u53EF\u4EE5\u975E\u5E38\u76F4\u89C2\u7684\u4E86\u89E3\u5F53\u524D\u7CFB\u7EDF\u4E1A\u52A1\u60C5\u51B5\uFF0C\u4E00\u65E6\u5F53\u524D QPS \u8D85\u8FC7\u6240\u8BBE\u5B9A\u7684\u9884\u8B66\u9600\u503C\u4EEC\u53EF\u4EE5\u8003\u8651\u589E\u52A0\u673A\u5668\u5BF9\u96C6\u7FA4\u6269\u5BB9\uFF0C\u4EE5\u514D\u538B\u529B\u8FC7\u5927\u5BFC\u81F4\u5B95\u673A\uFF0C\u53EF\u4EE5\u6839\u636E\u524D\u671F\u7684\u538B\u529B\u6D4B\u8BD5\u5F97\u5230\u4F30\u503C\uFF0C\u518D\u7ED3\u5408\u540E\u671F\u7EFC\u5408\u8FD0\u7EF4\u60C5\u51B5\uFF0C\u4F30\u7B97\u51FA\u9600\u503C\u3002</p><p><strong>\u539F\u7406\uFF1A</strong></p><p>\u6BCF\u592980%\u7684\u8BBF\u95EE\u96C6\u4E2D\u572820%\u7684\u65F6\u95F4\u91CC\uFF0C\u8FD920%\u65F6\u95F4\u53EB\u505A\u5CF0\u503C\u65F6\u95F4\u3002</p><p><strong>\u516C\u5F0F\uFF1A</strong></p><p>( \u603BPV\u6570 * 80% ) / ( \u6BCF\u5929\u79D2\u6570 * 20% ) = \u5CF0\u503C\u65F6\u95F4\u6BCF\u79D2\u8BF7\u6C42\u6570(QPS) \u3002</p><p><strong>\u7EDF\u8BA1\u65B9\u5F0F\uFF1A</strong></p><p>\u4E00\u822C\u4F7F\u7528 http_load \u8FDB\u884C\u7EDF\u8BA1</p><p><strong>\u9700\u8981\u673A\u5668\uFF1A</strong></p><p>\u5CF0\u503C\u65F6\u95F4\u6BCF\u79D2QPS / \u5355\u53F0\u673A\u5668\u7684QPS = \u9700\u8981\u7684\u673A\u5668 \u3002</p><p><strong>\u4F8B\u5B50\uFF1A</strong></p><p>\u6BCF\u5929300w PV \u7684\u5728\u5355\u53F0\u673A\u5668\u4E0A\uFF0C\u8FD9\u53F0\u673A\u5668\u9700\u8981\u591A\u5C11QPS\uFF1F</p><p><code>( 3000000 * 0.8 ) / (86400 * 0.2 ) = 139 (QPS)</code></p><h2 id="pv-\u8BBF\u95EE\u91CF-page-view" tabindex="-1">PV - \u8BBF\u95EE\u91CF (Page View) <a class="header-anchor" href="#pv-\u8BBF\u95EE\u91CF-page-view" aria-hidden="true">#</a></h2><p>\u7F51\u7AD9\u7684\u603B\u8BBF\u95EE\u91CF\uFF0C\u9875\u9762\u6D4F\u89C8\u91CF\u6216\u70B9\u51FB\u91CF\uFF0C\u7528\u6237\u6BCF\u5237\u65B0\u4E00\u6B21\u5C31\u4F1A\u88AB\u8BB0\u5F55\u4E00\u6B21\u3002</p><h2 id="uv-\u72EC\u7ACB\u8BBF\u5BA2-unique-visitor" tabindex="-1">UV - \u72EC\u7ACB\u8BBF\u5BA2 (Unique Visitor) <a class="header-anchor" href="#uv-\u72EC\u7ACB\u8BBF\u5BA2-unique-visitor" aria-hidden="true">#</a></h2><p>\u8BBF\u95EE\u7F51\u7AD9\u7684\u4E00\u53F0\u7535\u8111\u5BA2\u6237\u7AEF\u4E3A\u4E00\u4E2A\u8BBF\u5BA2\u300200:00 - 24:00\u5185\u76F8\u540C\u7684\u5BA2\u6237\u7AEF\u53EA\u4F1A\u88AB\u8BA1\u7B97\u4E00\u6B21\u3002</p><h2 id="iv" tabindex="-1">IV <a class="header-anchor" href="#iv" aria-hidden="true">#</a></h2><p>\u6307\u72EC\u7ACB IP \u8BBF\u95EE\u6570\uFF0C\u8BA1\u7B97\u662F\u4EE5\u4E00\u4E2A\u72EC\u7ACB\u7684 IP \u5728\u4E00\u4E2A\u8BA1\u7B97\u65F6\u6BB5\u5185\u8BBF\u95EE\u7F51\u7AD9\u8BA1\u7B97\u4E3A 1 \u6B21 IP \u8BBF\u95EE\u6570\u3002\u5728\u540C\u4E00\u4E2A\u8BA1\u7B97\u65F6\u6BB5\u5185\u4E0D\u7BA1\u8FD9\u4E2A IP \u8BBF\u95EE\u591A\u5C11\u6B21\u5747\u8BA1\u7B97\u4E3A 1 \u6B21\u3002\u8BA1\u7B97\u65F6\u6BB5\u6709\u4EE5 1 \u5929\u4E3A\u4E00\u4E2A\u8BA1\u7B97\u65F6\u6BB5\uFF0C\u4E5F\u6709\u4EE5 1 \u4E2A\u5C0F\u65F6\u4E3A\u4E00\u4E2A\u8BA1\u7B97\u65F6\u6BB5\u3002</p><h2 id="\u5E26\u5BBD" tabindex="-1">\u5E26\u5BBD <a class="header-anchor" href="#\u5E26\u5BBD" aria-hidden="true">#</a></h2><p><code>\u5E26\u5BBD\uFF08bps\uFF09= \u603B\u6D41\u91CF\u6570(bit) / \u4EA7\u751F\u6D41\u91CF\u7684\u65F6\u957F\uFF08\u79D2\uFF09=\uFF08PV * \u9875\u9762\u5E73\u5747\u5927\u5C0F * 8\uFF09/ \u7EDF\u8BA1\u65F6\u95F4\uFF08\u79D2\uFF09</code></p><p><strong>\u8BF4\u660E\uFF1A</strong></p><p>\u516C\u5F0F\u4E2D\u7684 8 \u6307\u7684\u662F\u5C06 Byte \u8F6C\u6362\u4E3A bit\uFF0C\u5373 8b/B\uFF0C\u56E0\u4E3A\u5E26\u5BBD\u7684\u5355\u4F4D\u662F bps\uFF08\u6BD4\u7279\u7387\uFF09\uFF0C\u5373bit per second\uFF0C\u6BCF\u79D2\u4E8C\u8FDB\u5236\u4F4D\u6570\uFF0C\u800C\u5BB9\u91CF\u5355\u4F4D\u4E00\u822C\u4F7F\u7528 Byte\u3002</p><p><strong>\u4F8B\u5B50\uFF1A</strong></p><p>\u5047\u8BBE\u67D0\u7AD9\u70B9\u7684\u65E5\u5747 PV \u662F 10w\uFF0C\u9875\u9762\u5E73\u5747\u5927\u5C0F 0.4 M\uFF0C\u90A3\u4E48\u5176\u5E73\u5747\u5E26\u5BBD\u9700\u6C42\u662F\uFF1A</p><p>\u5E73\u5747\u5E26\u5BBD = \uFF0810w * 0.4M * 8\uFF09 / \uFF0860 * 60 * 24\uFF09= 3.7 Mbps</p><p>\u4EE5\u4E0A\u8BA1\u7B97\u7684\u4EC5\u4EC5\u662F\u5E73\u5747\u5E26\u5BBD\uFF0C\u6211\u4EEC\u5728\u8FDB\u884C\u5BB9\u91CF\u9884\u4F30\u65F6\u9700\u8981\u7684\u662F\u5CF0\u503C\u5E26\u5BBD\uFF0C\u5373\u5FC5\u987B\u8981\u4FDD\u8BC1\u7AD9\u70B9\u5728\u5CF0\u503C\u6D41\u91CF\u65F6\u80FD\u591F\u6B63\u5E38\u8FD0\u8F6C\u3002\u5047\u8BBE\uFF0C\u5CF0\u503C\u6D41\u91CF\u662F\u5E73\u5747\u6D41\u91CF\u76845\u500D\uFF0C\u8FD9\u4E2A5\u500D\u79F0\u4E3A\u5CF0\u503C\u56E0\u5B50\u3002\u6309\u7167\u8FD9\u4E2A\u8BA1\u7B97\uFF0C\u5B9E\u9645\u9700\u8981\u7684\u5E26\u5BBD\u5927\u7EA6\u57283.7 Mbps * 5=18.5 Mbps \u3002</p><p>\u5E26\u5BBD\u9700\u6C42 = \u5E73\u5747\u5E26\u5BBD * \u5CF0\u503C\u56E0\u5B50</p><h2 id="\u5E76\u53D1\u7528\u6237\u6570" tabindex="-1">\u5E76\u53D1\u7528\u6237\u6570 <a class="header-anchor" href="#\u5E76\u53D1\u7528\u6237\u6570" aria-hidden="true">#</a></h2><p>\u5E76\u53D1\u7528\u6237\u6570\u662F\u6307\u7CFB\u7EDF\u53EF\u4EE5\u540C\u65F6\u627F\u8F7D\u7684\u6B63\u5E38\u4F7F\u7528\u7CFB\u7EDF\u529F\u80FD\u7684\u7528\u6237\u7684\u6570\u91CF\u3002</p><p>\u4E0E\u541E\u5410\u91CF\u76F8\u6BD4\uFF0C\u5E76\u53D1\u7528\u6237\u6570\u662F\u4E00\u4E2A\u66F4\u76F4\u89C2\u4F46\u4E5F\u66F4\u7B3C\u7EDF\u7684\u6027\u80FD\u6307\u6807\u3002\u5B9E\u9645\u4E0A\uFF0C\u5E76\u53D1\u7528\u6237\u6570\u662F\u4E00\u4E2A\u975E\u5E38\u4E0D\u51C6\u786E\u7684\u6307\u6807\uFF0C\u56E0\u4E3A\u7528\u6237\u4E0D\u540C\u7684\u4F7F\u7528\u6A21\u5F0F\u4F1A\u5BFC\u81F4\u4E0D\u540C\u7528\u6237\u5728\u5355\u4F4D\u65F6\u95F4\u53D1\u51FA\u4E0D\u540C\u6570\u91CF\u7684\u8BF7\u6C42\u3002\u4EE5\u7F51\u7AD9\u7CFB\u7EDF\u4E3A\u4F8B\uFF0C\u5047\u8BBE\u7528\u6237\u53EA\u6709\u6CE8\u518C\u540E\u624D\u80FD\u4F7F\u7528\uFF0C\u4F46\u6CE8\u518C\u7528\u6237\u5E76\u4E0D\u662F\u6BCF\u65F6\u6BCF\u523B\u90FD\u5728\u4F7F\u7528\u8BE5\u7F51\u7AD9\uFF0C\u56E0\u6B64\u5177\u4F53\u4E00\u4E2A\u65F6\u523B\u53EA\u6709\u90E8\u5206\u6CE8\u518C\u7528\u6237\u540C\u65F6\u5728\u7EBF\uFF0C\u5728\u7EBF\u7528\u6237\u5C31\u5728\u6D4F\u89C8\u7F51\u7AD9\u65F6\u4F1A\u82B1\u5F88\u591A\u65F6\u95F4\u9605\u8BFB\u7F51\u7AD9\u4E0A\u7684\u4FE1\u606F\uFF0C\u56E0\u800C\u5177\u4F53\u4E00\u4E2A\u65F6\u523B\u53EA\u6709\u90E8\u5206\u5728\u7EBF\u7528\u6237\u540C\u65F6\u5411\u7CFB\u7EDF\u53D1\u51FA\u8BF7\u6C42\u3002\u8FD9\u6837\uFF0C\u5BF9\u4E8E\u7F51\u7AD9\u7CFB\u7EDF\u6211\u4EEC\u4F1A\u6709\u4E09\u4E2A\u5173\u4E8E\u7528\u6237\u6570\u7684\u7EDF\u8BA1\u6570\u5B57\uFF1A\u6CE8\u518C\u7528\u6237\u6570\u3001\u5728\u7EBF\u7528\u6237\u6570\u548C\u540C\u65F6\u53D1\u8BF7\u6C42\u7528\u6237\u6570\u3002\u7531\u4E8E\u6CE8\u518C\u7528\u6237\u53EF\u80FD\u957F\u65F6\u95F4\u4E0D\u767B\u9646\u7F51\u7AD9\uFF0C\u4F7F\u7528\u6CE8\u518C\u7528\u6237\u6570\u4F5C\u4E3A\u6027\u80FD\u6307\u6807\u4F1A\u9020\u6210\u5F88\u5927\u7684\u8BEF\u5DEE\u3002\u800C\u5728\u7EBF\u7528\u6237\u6570\u548C\u540C\u4E8B\u53D1\u8BF7\u6C42\u7528\u6237\u6570\u90FD\u53EF\u4EE5\u4F5C\u4E3A\u6027\u80FD\u6307\u6807\u3002\u76F8\u6BD4\u800C\u8A00\uFF0C\u4EE5\u5728\u7EBF\u7528\u6237\u4F5C\u4E3A\u6027\u80FD\u6307\u6807\u66F4\u76F4\u89C2\u4E9B\uFF0C\u800C\u4EE5\u540C\u65F6\u53D1\u8BF7\u6C42\u7528\u6237\u6570\u4F5C\u4E3A\u6027\u80FD\u6307\u6807\u66F4\u51C6\u786E\u4E9B\u3002</p>',43),n=[i];function s(d,h,o,l,c,u){return r(),t("div",null,n)}const v=e(a,[["render",s]]);export{_ as __pageData,v as default};
